Files.File:
using System;

namespace ${Namespace}
{
	class MainClass
	{
		public static void Main (string[] args)
		{
			Console.WriteLine("Hello, Tizen!");
		}
	}
}

Directory.File:
#include <FApp.h>
#include <FBase.h>
#include <FSystem.h>
#include <FUi.h>

#include <new>

using namespace Tizen::Base;
using namespace Tizen::Base::Collection;

#ifdef __cplusplus
extern "C"
{
#endif // __cplusplus

#include <mono/jit/jit.h>
#include <mono/metadata/environment.h>
#include <mono/metadata/mono-config.h>
#include <stdlib.h>

static void
mono_tizen_jit_exec_main (MonoDomain *domain, const char *file, int argc, char** argv)
{
	MonoAssembly *assembly;

	assembly = mono_domain_assembly_open (domain, file);
	if (!assembly)
		exit (2);
	/*
	 * mono_jit_exec() will run the Main() method in the assembly.
	 * The return value needs to be looked up from
	 * System.Environment.ExitCode.
	 */
	mono_jit_exec (domain, assembly, argc, argv);
}

int
mono_tizen_main(int argc, char* argv[]) {
	MonoDomain *domain;
	const char *file;
	int retval;

	file = "${Namespace}.dll";

	/*
	 * Load the default Mono configuration file, this is needed
	 * if you are planning on using the dllmaps defined on the
	 * system configuration
	 */
	mono_config_parse (NULL);
	/*
	 * mono_jit_init() creates a domain: each assembly is
	 * loaded and run in a MonoDomain.
	 */
	domain = mono_jit_init (file);

	mono_tizen_jit_exec_main (domain, file, argc, argv);

	retval = mono_environment_exitcode_get ();

	mono_jit_cleanup (domain);
	return retval;
}

//
// The framework calls this method as the entry method of the Tizen application.
//
_EXPORT_ int
OspMain(int argc, char* pArgv[])
{
	AppLog("Application started.");

        int r = mono_tizen_main(argc, pArgv);

	TryLog(r != 0, "[%d] Application execution failed.", r);
	AppLog("Application finished.");

	return r;
}
#ifdef __cplusplus
}
#endif // __cplusplus
